<template>
  <div class="l_pie_wrapper">
    <div id="l_pie"></div>
  </div>
</template>

<script>
export default {
  name: 'LTopPie',
  data() {
    return {
      Echart: null,
    };
  },
  methods: {
    chartsInit() {
      if (!this.Echart) {
        this.Echart = this.$echarts.init(document.getElementById('l_pie'));
      }
      const img = 'data:image/png;base64,';
      const man = 'data:image/png;base64,';
      const woman = 'data:image/png;base64,';
      const sexData = [{
        name: '男性',
        value: 245,
      }, {
        name: '女性',
        value: 327,
      }];

      const data = [];
      const color = ['#00FFFF', '#C424FF', '#ffa800', '#ff5b00', '#ff3000'];
      for (let i = 0; i < sexData.length; i += 1) {
        data.push({
          value: sexData[i].value,
          name: sexData[i].name,
          itemStyle: {
            normal: {
              borderWidth: 5,
              shadowBlur: 20,
              borderColor: color[i],
              shadowColor: color[i],
            },
          },
        }, {
          value: 2,
          name: '',
          itemStyle: {
            normal: {
              label: {
                show: false,
              },
              labelLine: {
                show: false,
              },
              color: 'rgba(0, 0, 0, 0)',
              borderColor: 'rgba(0, 0, 0, 0)',
              borderWidth: 0,
            },
          },
        });
      }
      const seriesOption = [{
        name: '',
        type: 'pie',
        clockWise: false,
        radius: [45, 45],
        hoverAnimation: false,
        itemStyle: {
          normal: {
            label: {
              show: false,
              position: [0, -20],
              color: '#ddd',
              formatter(params) {
                let percent = 0;
                let total = 0;
                for (let i = 0; i < sexData.length; i += 1) {
                  total += sexData[i].value;
                }
                percent = ((params.value / total) * 100).toFixed(0);
                if (params.name !== '') {
                  return `${params.name}：${percent}%`;
                }
                return '';
              },
            },
            labelLine: {
              length: 10,
              length2: 10,
              show: false,
              color: '#00ffff',
            },
          },
        },
        data,
      }];
      const option = {

        color,
        graphic: {
          elements: [{
            type: 'image',
            z: 3,
            style: {
              image: img,
              width: 70,
              height: 70,
            },
            left: 'center',
            top: 'center',
            position: [100, 100],
          }, {
            type: 'image',
            z: 3,
            style: {
              image: woman,
              width: 25,
              height: 40,
            },
            left: '50%',
            top: 'center',
          },
          {
            type: 'image',
            z: 3,
            style: {
              image: man,
              width: 25,
              height: 40,
            },
            right: '50%',
            top: 'center',
          },
          ],
        },
        tooltip: {
          show: false,
        },
        toolbox: {
          show: false,
        },
        series: seriesOption,
      };
      this.Echart.setOption(option);
    },
  },
  mounted() {
    this.chartsInit();
  },
};
</script>

<style lang="scss" scoped>
.l_pie_wrapper{
  width:100%;
  height:100%;
  #l_pie{
    width:100%;
    height:100%;
  }
}
</style>
